import axios from 'axios';
import { JOB_ERROR, JOB_PROFILE, JOB_SAVE } from './types';
import { setAlert } from './alert';

// get all jobs
export const getJobs = () => async dispatch => {
    try {
        const res = await axios.get('/jobs');
        console.log(res.data);
        dispatch({
            type:JOB_PROFILE,
            payload: res.data
        });
    } catch (err) {
        dispatch({
            type:JOB_ERROR,
            payload:{msg: err.response.statusText, status: err.response.status }
        });
    }
};

// save job
export const saveJob = (item) => async dispatch => {
    console.log(item);
    try {
        await axios.post('/savejob');
        dispatch(setAlert('job saved successfully', 'success'));
    } catch (err) {
        dispatch({
            type:JOB_ERROR,
            payload: { msg: err.response.statusText, status: err.response.status}
        })
    }
}
// reject job
export const rejectJob = (item) => async dispatch => {
    console.log(item);
    try {
        await axios.post('/rejectjob');
        dispatch(setAlert('You have reject this job', 'success'));
    } catch (err) {
        dispatch({
            type:JOB_ERROR,
            payload: { msg: err.response.statusText, status: err.response.status}
        })
    }
}

// apply job
export const applyJob = (item) => async dispatch => {
    console.log(item);
    try {
        await axios.post('/appliedjob');
        dispatch(setAlert('You have successfully applied to this job', 'success'));
    } catch (err) {
        dispatch({
            type:JOB_ERROR,
            payload: { msg: err.response.statusText, status: err.response.status}
        })
    }
}
