import {GET_JOBS, JOB_ERROR} from '../actions/types';
const initialState = {
	list: null,
	list: [],
	loading: true,
	error: {}
}

export default function (state = initialState, action) {
    const { type, payload } = action;

    switch (type) {
        case GET_JOBS:
            return {
                ...state,
				lists: payload,
				loading: false
            };
            case JOB_ERROR:
                return {
                    ...state,
				error: payload,
				loading: false,
				list: null
                }
    default:
        return state
    };
}
