import GET_JOB from '../actions/types';
const initialState = {
    jobItem: null,
    jobLists: [],
    loading: true,
    error: {}
}

export default function (state = initialState, action) {
    const { type, payload } = action;

    switch (type) {
        case GET_JOB:
            return {
                ...state,
                jobItem: payload,
                loading: false,
            };
    default:
        return state
    };
}
