import axios from 'axios';
import { setAlert } from './alert';

import {
    GET_JOBS, JOB_ERROR
} from './types';

// get all jobs

export const getAllJobs = () => async dispatch => {
    try {
        const res = await axios.get('/jobs');
        console.log(res);
        dispatch({
            type:GET_JOBS,
            payload: res.data
        });
    } catch (err) {
        dispatch({
            type:JOB_ERROR,
            payload:{msg: err.response.statusText, status: err.response.status }
        })
    }
}
