import axios from 'axios';

const setAuthToken = headerToken => {
  if (headerToken) {
    axios.defaults.headers.common['Authorization'] = headerToken;
  } else {
    delete axios.defaults.headers.common['Authorization'];
  }
};

export default setAuthToken;
