import {GET_JOBS} from '../actions/types';
const initialState = {
	profile: null,
	profiles: [],
	repos: [],
	loading: true,
	error: {}
}

export default function (state = initialState, action) {
    const { type, payload } = action;

    switch (type) {
        case GET_JOBS:
            return {
                ...state,
				profiles: payload,
				loading: false
            };
    default:
        return state
    };
}
