import {
    GET_JOB,
    GET_JOBS,
    GET_SAVED_JOBS,
    GET_REJECTED_JOBS,
    GET_APPLIED_JOBS,
    JOB_ERROR
} from '../actions/jobs';

const initialState = {
    jobItem: null,
    jobLists: [],
    loading: true,
    error: {}
}

export default function (state = initialState, action) {
    const { type, payload } = action;

    switch (type) {
        case GET_JOBS:
            return {
                ...state,
                jobItem: payload,
                loading: false,
            };
    default:
        return state
    };
}
